climate::ClimateTraits GreeIRClimate::traits() {
  auto traits = climate::ClimateTraits();
  traits.set_supports_current_temperature(this->sensor_ != nullptr);
  traits.set_supported_modes({
      climate::CLIMATE_MODE_OFF,
      climate::CLIMATE_MODE_COOL,
      climate::CLIMATE_MODE_HEAT,
  });
  traits.set_supports_two_point_target_temperature(false);
  traits.set_visual_min_temperature(this->minimum_temperature_);
  traits.set_visual_max_temperature(this->maximum_temperature_);
  traits.set_visual_temperature_step(this->temperature_step_);
  traits.set_supported_fan_modes(this->fan_modes_);
  traits.set_supported_swing_modes(this->swing_modes_);
  traits.set_supported_presets(this->presets_);
  return traits;
}

void GreeIRClimate::transmit_state() {
  auto transmit = this->transmitter_->transmit(); // TransmitCall
  auto data = transmit.get_data();                // RemoteTransmitData
  data->set_carrier_frequency(38000);

  const std::vector<int32_t> &code =
      match(this->mode, this->fan_mode.value(), this->temperature_());
  data->set_data(code);

  transmit.perform();
}

uint8_t GreeIRClimate::temperature_() {
  return (uint8_t)roundf(
      clamp<float>(this->target_temperature, GREE_TEMP_MIN, GREE_TEMP_MAX));
}

